import React, {useState, useEffect, useRef} from "react";
import { useOutletContext } from "react-router-dom";
import { useParams } from "react-router-dom";
import axiosInstance from "../axiosApi";
import {
    ListGroup,
    ListGroupItem,
    Dropdown,
    Popover,
    DropdownMenu,
    PopoverBody,
    DropdownToggle,
    DropdownItem
} from 'reactstrap'

const TodoDashboard = () => {
    const [toggleRefreshProjectlist] = useOutletContext();
    const [popoverOpen, togglePopover] = useState(false);
    const [sortResult, setSortResult] = useState("default");
    const [queryResult, setQueryResult] = useState([]);
    const { projectid } = useParams();
    const $titleInputRef = useRef();
    const [todoError, setTodoError] = useState(null);

    const haddlePopover=()=>{
        togglePopover(!popoverOpen);
    }
    const sortDropdown=(event)=>{
        setSortResult(event.target.getAttribute("data-type"));
    }

    const handleCreateClick=()=>{
        toggleProjectAdd(!modalProject);
    }

    const handleTitleChange = (event) => {
        setTodoError(null);

        const title = $titleInputRef.current.value;
        if (title === queryResult["description"]) return;

        const errors = [];
        if(!title) errors.push("Title is required");
        if(title.lenght > 20) errors.push("Max length of Title is 20");

        if (errors.length > 0) {
            setTodoError(errors);
        } else {
            try {
                event.preventDefault();
                const axios = axiosInstance.put(`/project/${projectid}`, {
                    description: title,
                    flag: queryResult.flag,
                    sequence: queryResult.sequence
                });
                axios.then(res => {
                    const data = {type:"project",message:"The new Project is created"};
                    getProjectDetails();
                    toggleRefreshProjectlist(1 + Math.random() * (1000 - 1));
                }).catch(({response}) => {
                    //props.handleAlert(response.data.detail);
                });

            } catch (error) {
                throw error;
            }
        }
    };

    useEffect(() => {
        getProjectDetails();
    }, [projectid]);

    const getProjectDetails = () => {
        const axios = axiosInstance.get(`/project/${projectid}/todo`);
        axios.then(res => {
            setQueryResult(res.data[0]);
        }).catch(({response}) => {

        });
    }

    return (
        <section className="vh-100 bg-light p-3">

            <div className="d-flex  justify-content-between pb-2">
                <div>
                    <textarea 
                        className="editable-textarea" 
                        defaultValue={queryResult["description"]}
                        ref={$titleInputRef}
                        onBlur={handleTitleChange}
                        onKeyDown={event => {
                            if (event.keyCode === 13) {
                                event.target.blur();
                            }
                        }}
                    />
                </div>
                <div className="ps-2">
                    <div className="dropdown">
                        <button id="mypopover" className="btn p-0 m-0 " type="button" >
                            <i className="bi bi-sliders"></i>
                        </button>
                        <Popover
                            placement="bottom"
                            isOpen={popoverOpen}
                            target="mypopover"
                            toggle={haddlePopover}

                            trigger='legacy'

                            offset={[-50,0]}
                        >
                            <PopoverBody >
                                <ul className="list-group">
                                    <li className="list-group-item border-0 w-100 p-0 m-0">
                                        <div className="p-2"><i className="bi bi-plus-circle"></i> Add new Task  </div>
                                    </li>
                                    <li className="list-group-item border-0 w-100 p-0 m-0">
                                        <div className="d-flex   w-100 justify-content-between">
                                            <div className="p-2"><i className="bi bi-sort-down"></i> Sort By  </div>

                                            <div className="dropdown  p-1 m-0">

                                                <button className="btn dropdown-toggle btn-sm" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">
                                                {sortResult}
                                                </button>
                                                <ul className="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                                                    <li><a className="dropdown-item" data-type="Priority" href="#" onClick={sortDropdown}>Priority</a></li>
                                                    <li><a className="dropdown-item" data-type="Assignee" href="#" onClick={sortDropdown}>Assignee</a></li>
                                                    <li><a className="dropdown-item" data-type="Project" href="#" onClick={sortDropdown}>Project</a></li>
                                                </ul>
                                            </div>
                                        </div>
                                    </li>
                                </ul>
                            </PopoverBody>
                        </Popover>
                    </div>

                </div>
            </div>
            <ListGroup>
                <ListGroupItem action href="#" tag="a"  >
                    <div className="d-flex justify-content-between">
                        <div>
                            <i className="bi bi-check-circle pe-2"></i>
                            Fon's asshole itchy :D
                            <span className="badge bg-danger ms-2">High</span>
                        </div>
                        <div className="onhover-wrap">
                            <button type="button" className="btn btn-light onhover-button"><i className="bi bi-card-text"></i></button>
                            <button type="button" className="btn btn-light onhover-button"><i className="bi bi-calendar-event"></i></button>
                        </div>
                    </div>
                </ListGroupItem>
                <ListGroupItem action href="#" tag="a" >
                    <div className="d-flex justify-content-between">
                        <div>
                            <i className="bi bi-check-circle pe-2"></i>
                            Mybababy has a big dick
                            <span className="badge bg-warning ms-2">Medium</span>
                        </div>
                        <div className="onhover-wrap">
                            <button type="button" className="btn btn-light onhover-button"><i className="bi bi-card-text"></i></button>
                            <button type="button" className="btn btn-light onhover-button"><i className="bi bi-calendar-event"></i></button>
                        </div>
                    </div>
                </ListGroupItem>
                <ListGroupItem action href="#" tag="a" >
                    <i className="bi bi-circle"></i>  Panda shit on the macbook
                </ListGroupItem>
                <ListGroupItem action href="#" tag="a" >
                    <i className="bi bi-circle"></i> Grizz want to slap panda ass
                </ListGroupItem>
                <ListGroupItem action href="#" tag="a" >
                    <i className="bi bi-check-circle"></i> Ice bear is a good guy
                </ListGroupItem>
            </ListGroup>

        </section>

    )
}



export default TodoDashboard;
