import React, {Fragment} from "react";
import axiosInstance from "../axiosApi";
import {
    ModalHeader,
    ModalBody,
    ModalFooter,
    Button,
    Form,
    FormGroup,
    Label,
    Input
} from "reactstrap";

const CreateProject = (props) =>{
    const handleSubmit= (event)=>{
        try {
            event.preventDefault();
            const data = axiosInstance.post('/project/', {
                sequence:100000000,
                description: event.target.description.value,
                flag: event.target.flag.value,
                created_by: localStorage.getItem("userid")
            });
            axiosInstance.defaults.headers['Authorization'] = "JWT " + data.access;

           // window.location.href = "/";
        } catch (error) {
            throw error;
        }
    }

        return (
            <Fragment>
                <ModalHeader toggle={props.handleCreateClick}>Create New Project</ModalHeader>
                <ModalBody>
                    <Form id="create_project_form" onSubmit={handleSubmit}>
                        <FormGroup>
                            <Label >Name</Label>
                            <Input type="text" name="description" />
                        </FormGroup>
                        <FormGroup className="form-switch">
                            <Input type="checkbox" name="flag" />
                            <Label className="ps-2">Flag the project </Label>
                        </FormGroup>
                    </Form>
                </ModalBody>
                <ModalFooter>
                    <Button color="primary" type="submit" form="create_project_form">Submit</Button>{' '}
                    <Button color="secondary" onClick={props.handleCreateClick}>Cancel</Button>
                </ModalFooter>
            </Fragment>
        )

}

export default CreateProject;
